import { useCallback, useEffect, useState } from 'react';

import { useRecoilValue, useResetRecoilState } from 'recoil';
import { useRouter } from 'next/router';
import omitBy from 'lodash-es/omitBy';
import isUndefined from 'lodash-es/isUndefined';
import { useMutation } from '@tanstack/react-query';
import { Box, Flexbox, Typography, useTheme } from '@mrcamelhub/camel-ui';
import styled from '@emotion/styled';

import LocalStorage from '@library/localStorage';
import { logEvent } from '@library/amplitude';

import { fetchArea, postArea } from '@api/user';

import {
  IS_DONE_SIGN_IN_PERMISSION,
  MODEL_CATEGORY_IDS,
  ONBOARDING_SKIP_USERIDS,
  SELECTED_MODEL_CARD,
  SHOW_PRODUCTS_KEYWORD_POPUP,
  SIGN_UP_STEP
} from '@constants/localStorage';
import attrProperty from '@constants/attrProperty';
import attrKeys from '@constants/attrKeys';

import { checkAgent } from '@utils/common';

import type { FindLocation } from '@typings/common';
import { searchParamsState } from '@recoil/searchHelper';
import { modelParentCategoryIdsState, selectedModelCardState } from '@recoil/onboarding';
import useQueryAccessUser from '@hooks/useQueryAccessUser';
import useMutationPutAlarm from '@hooks/useMutationPutAlarm';

import OnboardingBottomCTA from './OnboardingBottomCTA';

const permissionContents = [
  {
    icon: <PinIcon />,
    title: '위치',
    subTitle: '거래가능한 당근마켓 매물을 보여드리기 위해 필요해요!'
  },
  {
    icon: <AlarmIcon />,
    title: '알림',
    subTitle: '찜한 매물의 가격하락, 꿀매물, 감정결과를 알려드려요!'
  },
  {
    icon: '📷',
    title: '사진 및 카메라',
    subTitle: '내 물건 등록, 사진 감정을 하려면 필요해요!'
  }
];

function OnboardingPermission() {
  const {
    theme: {
      palette: { common }
    }
  } = useTheme();

  const router = useRouter();

  const {
    keyword,
    brandIds,
    parentIds,
    subParentIds,
    categorySizeIds,
    lineIds,
    minPrice,
    maxPrice,
    idFilterIds,
    siteUrlIds,
    colorIds,
    seasonIds,
    materialIds
  } = useRecoilValue(searchParamsState);
  const resetSearchParams = useResetRecoilState(searchParamsState);
  const { mutate: mutatePutAlarm } = useMutationPutAlarm();
  const { mutate: mutatePostArea } = useMutation(postArea);
  const { data: acessUser } = useQueryAccessUser();
  const removeModelParentCategoryId = useResetRecoilState(modelParentCategoryIdsState);
  const removeModelCard = useResetRecoilState(selectedModelCardState);
  const [pending, setPending] = useState(false);

  useEffect(() => {
    logEvent(attrKeys.welcome.VIEW_AUTH_MODAL, {
      name: attrProperty.name.PERSONAL_INPUT
    });
  }, []);

  const redirectPage = useCallback(() => {
    // 검색집사 완료 후 매물목록 저장 유도 팝업을 통해 로그인 한 경우
    if (LocalStorage.get(SHOW_PRODUCTS_KEYWORD_POPUP)) {
      LocalStorage.remove(SHOW_PRODUCTS_KEYWORD_POPUP);
      router.replace({
        pathname: `/products/search/${keyword}`,
        query: omitBy(
          {
            brandIds,
            parentIds,
            subParentIds,
            categorySizeIds,
            lineIds,
            minPrice,
            maxPrice,
            idFilterIds,
            siteUrlIds,
            colorIds,
            seasonIds,
            materialIds
          },
          isUndefined
        )
      });
      resetSearchParams();
      return;
    }

    router.replace('/');
  }, [
    brandIds,
    categorySizeIds,
    colorIds,
    idFilterIds,
    keyword,
    lineIds,
    materialIds,
    maxPrice,
    minPrice,
    parentIds,
    resetSearchParams,
    router,
    seasonIds,
    siteUrlIds,
    subParentIds
  ]);

  const onboardingSkipUserManage = useCallback(() => {
    const userIds = (LocalStorage.get(ONBOARDING_SKIP_USERIDS) as number[]) || [];

    if (userIds.length) {
      LocalStorage.set(ONBOARDING_SKIP_USERIDS, [...userIds, Number(acessUser?.userId)]);
    } else {
      LocalStorage.set(ONBOARDING_SKIP_USERIDS, [Number(acessUser?.userId)]);
    }
  }, [acessUser?.userId]);

  const handleClick = useCallback(() => {
    setPending(true);

    // mutatePostStyle({ parentCategoryIds: null, subParentCategoryIds: null });
    LocalStorage.remove(SIGN_UP_STEP);
    LocalStorage.remove(MODEL_CATEGORY_IDS);
    // LocalStorage.remove(SELECTED_STYLE_CARD_IDS);
    LocalStorage.remove(SELECTED_MODEL_CARD);
    LocalStorage.set(IS_DONE_SIGN_IN_PERMISSION, true);
    onboardingSkipUserManage();
    removeModelParentCategoryId();
    removeModelCard();

    if (
      checkAgent.isAndroidApp() &&
      window.webview &&
      window.webview.callAuthPush &&
      window.webview.callAuthLocation &&
      window.webview.callAuthCamera &&
      window.webview.callAuthPhotoLibrary
    ) {
      window.webview.callAuthPush();
      window.webview.callAuthLocation();
      window.webview.callAuthCamera();
      window.webview.callAuthPhotoLibrary();
      return;
    }

    if (
      checkAgent.isIOSApp() &&
      window.webkit &&
      window.webkit.messageHandlers &&
      window.webkit.messageHandlers.callAuthPush &&
      window.webkit.messageHandlers.callAuthLocation &&
      window.webkit.messageHandlers.callAuthCamera &&
      window.webkit.messageHandlers.callAuthPhotoLibrary
    ) {
      window.webkit.messageHandlers.callAuthPush.postMessage(0);
      window.webkit.messageHandlers.callAuthLocation.postMessage(0);
      window.webkit.messageHandlers.callAuthCamera.postMessage(0);
      window.webkit.messageHandlers.callAuthPhotoLibrary.postMessage(0);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { longitude, latitude } = position.coords;

        if (longitude > 0 || latitude > 0) {
          const { name, x, y } = await fetchArea({ x: String(longitude), y: String(latitude) });

          mutatePostArea(
            { name, x, y },
            {
              onSuccess: () => redirectPage(),
              onError: () => redirectPage()
            }
          );
        } else {
          redirectPage();
        }
      },
      () => {
        redirectPage();
      }
    );
  }, [
    onboardingSkipUserManage,
    removeModelParentCategoryId,
    removeModelCard,
    mutatePostArea,
    redirectPage
  ]);

  useEffect(() => {
    window.getAuthPush = (result: boolean) => {
      if (result) {
        mutatePutAlarm({
          isNotiEvent: true,
          isNotiChannel: true
        });
      }
    };

    window.getAuthLocation = async ({ lng, lat }: FindLocation) => {
      if (lng || lat) {
        const { name, x, y } = await fetchArea({ x: lng, y: lat });

        mutatePostArea(
          { name, x, y },
          {
            onSuccess: () => router.push('/'),
            onError: () => router.push('/')
          }
        );
      } else {
        router.replace('/');
      }
    };
  }, [mutatePutAlarm, mutatePostArea, router]);

  return (
    <>
      <Box
        customStyle={{
          backgroundColor: common.uiBlack,
          borderRadius: '16px 16px 0 0',
          padding: '32px 20px'
        }}
      >
        <Typography
          variant="h3"
          weight="bold"
          customStyle={{ color: common.ui98, marginBottom: 20 }}
        >
          카멜을 더 잘 즐기기 위해
          <br />
          권한을 허용해주세요
        </Typography>
        <Flexbox gap={20} direction="vertical" customStyle={{ paddingBottom: 20, flex: 1 }}>
          {permissionContents.map((content) => (
            <Flexbox gap={12} alignment="center" key={`permisstion-contents-${content.title}`}>
              <IconBox>{content.icon}</IconBox>
              <Box>
                <Typography weight="bold" customStyle={{ color: common.ui98 }}>
                  {content.title}
                </Typography>
                <Typography variant="body2" customStyle={{ color: common.ui60, marginTop: 2 }}>
                  {content.subTitle}
                </Typography>
              </Box>
            </Flexbox>
          ))}
        </Flexbox>
      </Box>
      <OnboardingBottomCTA onClick={handleClick} disabled={pending} isResult>
        시작하기
      </OnboardingBottomCTA>
    </>
  );
}

const IconBox = styled.div`
  height: 52px;
  width: 52px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: ${({
    theme: {
      palette: { common }
    }
  }) => common.ui20};
  font-size: ${({ theme: { typography } }) => typography.h2.size};
`;

function AlarmIcon() {
  return (
    <svg
      width="24"
      height="25"
      viewBox="0 0 24 25"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      xmlnsXlink="http://www.w3.org/1999/xlink"
    >
      <path d="M0 24.68H24V0.68H0V24.68Z" fill="url(#pattern0)" />
      <defs>
        <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
          <use xlinkHref="#image0_1190_1222" transform="scale(0.015625)" />
        </pattern>
        <image
          id="image0_1190_1222"
          width="64"
          height="64"
          xlinkHref="data:image/png;base64,"
        />
      </defs>
    </svg>
  );
}

function PinIcon() {
  return (
    <svg
      width="24"
      height="25"
      viewBox="0 0 24 25"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      xmlnsXlink="http://www.w3.org/1999/xlink"
    >
      <path d="M0 24.68H24V0.68H0V24.68Z" fill="url(#pattern1)" />
      <defs>
        <pattern id="pattern1" patternContentUnits="objectBoundingBox" width="1" height="1">
          <use xlinkHref="#image0_1190_3909" transform="scale(0.015625)" />
        </pattern>
        <image
          id="image0_1190_3909"
          width="64"
          height="64"
          xlinkHref="data:image/png;base64,"
        />
      </defs>
    </svg>
  );
}

export default OnboardingPermission;
